import React, { useState, useEffect } from 'react';

interface TimerDisplayProps {
    isActive: boolean;
    startTime: string | null;
}

export const TimerDisplay: React.FC<TimerDisplayProps> = ({ isActive, startTime }) => {
    // We only use state for visual updates, the math is absolute based on startTime
    const [elapsedSeconds, setElapsedSeconds] = useState(0);

    useEffect(() => {
        let interval: ReturnType<typeof setInterval>;

        if (isActive && startTime) {
            const startMs = new Date(startTime).getTime();

            // Immediate update to avoid 1-second delay
            setElapsedSeconds(Math.max(0, Math.floor((Date.now() - startMs) / 1000)));

            interval = setInterval(() => {
                setElapsedSeconds(Math.max(0, Math.floor((Date.now() - startMs) / 1000)));
            }, 1000);
        } else if (!isActive) {
            setElapsedSeconds(0);
        }

        return () => {
            if (interval) clearInterval(interval);
        };
    }, [isActive, startTime]);

    const formatTime = (totalSeconds: number) => {
        const h = Math.floor(totalSeconds / 3600);
        const m = Math.floor((totalSeconds % 3600) / 60);
        const s = totalSeconds % 60;

        return {
            hours: h.toString().padStart(2, '0'),
            minutes: m.toString().padStart(2, '0'),
            seconds: s.toString().padStart(2, '0')
        };
    };

    const time = formatTime(elapsedSeconds);

    return (
        <div className="font-mono text-[6rem] sm:text-8xl md:text-[10rem] lg:text-[12rem] leading-none tracking-tighter text-zinc-900 dark:text-zinc-50 transition-all duration-700 tabular-nums">
            {elapsedSeconds >= 3600 && (
                <>
                    <span>{time.hours}</span>
                    <span className="text-zinc-300 dark:text-zinc-700 transition-opacity">:</span>
                </>
            )}
            <span>{time.minutes}</span>
            <span className="text-zinc-300 dark:text-zinc-700 transition-opacity">:</span>
            <span>{time.seconds}</span>
        </div>
    );
};

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { Slot } from "radix-ui"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-white hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost:
          "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2 has-[>svg]:px-3",
        xs: "h-6 gap-1 rounded-md px-2 text-xs has-[>svg]:px-1.5 [&_svg:not([class*='size-'])]:size-3",
        sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
        icon: "size-9",
        "icon-xs": "size-6 rounded-md [&_svg:not([class*='size-'])]:size-3",
        "icon-sm": "size-8",
        "icon-lg": "size-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

function Button({
  className,
  variant = "default",
  size = "default",
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean
  }) {
  const Comp = asChild ? Slot.Root : "button"

  return (
    <Comp
      data-slot="button"
      data-variant={variant}
      data-size={size}
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  )
}

export { Button, buttonVariants }

"use client"

import * as React from "react"
import type { Label as LabelPrimitive } from "radix-ui"
import { Slot } from "radix-ui"
import {
  Controller,
  FormProvider,
  useFormContext,
  useFormState,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState } = useFormContext()
  const formState = useFormState({ name: fieldContext.name })
  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

function FormItem({ className, ...props }: React.ComponentProps<"div">) {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div
        data-slot="form-item"
        className={cn("grid gap-2", className)}
        {...props}
      />
    </FormItemContext.Provider>
  )
}

function FormLabel({
  className,
  ...props
}: React.ComponentProps<typeof LabelPrimitive.Root>) {
  const { error, formItemId } = useFormField()

  return (
    <Label
      data-slot="form-label"
      data-error={!!error}
      className={cn("data-[error=true]:text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
}

function FormControl({ ...props }: React.ComponentProps<typeof Slot.Root>) {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot.Root
      data-slot="form-control"
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
}

function FormDescription({ className, ...props }: React.ComponentProps<"p">) {
  const { formDescriptionId } = useFormField()

  return (
    <p
      data-slot="form-description"
      id={formDescriptionId}
      className={cn("text-muted-foreground text-sm", className)}
      {...props}
    />
  )
}

function FormMessage({ className, ...props }: React.ComponentProps<"p">) {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : props.children

  if (!body) {
    return null
  }

  return (
    <p
      data-slot="form-message"
      id={formMessageId}
      className={cn("text-destructive text-sm", className)}
      {...props}
    >
      {body}
    </p>
  )
}

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { TOKEN_KEY, AUTH_EXPIRED_EVENT, api } from './api';

// Define the shape of our User based on the backend DTO
export interface User {
    id: string;
    email: string;
    name: string;
    tenantId: string;
    tenantName: string;
    role: string;
}

interface AuthContextType {
    user: User | null;
    isAuthenticated: boolean;
    isLoading: boolean;
    login: (token: string) => Promise<void>;
    logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [user, setUser] = useState<User | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    const logout = useCallback(() => {
        localStorage.removeItem(TOKEN_KEY);
        setUser(null);
    }, []);

    // Listen for 401 auth expired events from the Axios interceptor
    useEffect(() => {
        const handleExpired = () => logout();
        window.addEventListener(AUTH_EXPIRED_EVENT, handleExpired);
        return () => window.removeEventListener(AUTH_EXPIRED_EVENT, handleExpired);
    }, [logout]);

    // Check for token on mount and fetch user profile
    useEffect(() => {
        const initAuth = async () => {
            const token = localStorage.getItem(TOKEN_KEY);
            if (token) {
                try {
                    const { data } = await api.get('/users/me');
                    setUser(data);
                } catch (error) {
                    console.error('Failed to authenticate token during app load', error);
                    localStorage.removeItem(TOKEN_KEY);
                    setUser(null);
                }
            }
            setIsLoading(false);
        };

        initAuth();
    }, []);

    const login = async (token: string) => {
        localStorage.setItem(TOKEN_KEY, token);

        try {
            setIsLoading(true);
            const { data } = await api.get('/users/me');
            setUser(data);
        } catch (error) {
            console.error('Failed to fetch user after login', error);
            logout();
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <AuthContext.Provider value={{
            user,
            isAuthenticated: !!user,
            isLoading,
            login,
            logout
        }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../lib/auth-context';
import { authApi } from '../../lib/api';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardFooter } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Link } from 'react-router-dom';

export const Register: React.FC = () => {
    const { login } = useAuth();

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        companyName: ''
    });
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData(prev => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleSubmit = async (e: React.SyntheticEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        if (formData.password !== formData.confirmPassword) {
            setError('As senhas n├úo coincidem. Tente novamente.');
            setIsLoading(false);
            return;
        }

        try {
            const { confirmPassword: _, ...apiPayload } = formData;
            const response = await authApi.post('/register', apiPayload);

            if (response.data?.token) {
                await login(response.data.token);
            } else {
                setError('Conta criada, mas o token n├úo foi retornado. Tente fazer login.');
            }
        } catch (err: unknown) {
            if (axios.isAxiosError(err)) {
                setError(err.response?.data?.message || 'Falha ao criar a conta. Verifique os dados.');
            } else {
                setError('Erro inesperado. Tente novamente.');
            }
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="flex min-h-screen items-center justify-center bg-zinc-50 px-4 py-12 dark:bg-zinc-950">
            <div className="w-full max-w-md space-y-6">
                <div className="flex flex-col items-center justify-center space-y-2 text-center">
                    <Link to="/" className="flex h-16 items-center justify-center mb-2 transition-transform hover:scale-105">
                        <img src="/qronis_ext.svg" alt="Qronis Logo" className="h-12 w-auto object-contain drop-shadow-sm" />
                    </Link>
                    <h1 className="text-2xl font-bold tracking-tight text-zinc-900 dark:text-zinc-50">Crie seu Workspace</h1>
                    <p className="text-sm text-zinc-500 dark:text-zinc-400">
                        Alguns dados b├ísicos e voc├¬ estar├í pronto para o Zen Mode.
                    </p>
                </div>

                <Card className="border-0 shadow-xl dark:border dark:border-zinc-800 dark:bg-zinc-900/50">
                    <form onSubmit={handleSubmit}>
                        <CardContent className="flex flex-col gap-4 p-6 pt-2">
                            {error && (
                                <div className="rounded-md bg-red-50 p-3 text-sm text-red-600 dark:bg-red-900/30 dark:text-red-400">
                                    {error}
                                </div>
                            )}

                            <div className="space-y-2">
                                <Label htmlFor="name">Seu Nome</Label>
                                <Input
                                    id="name"
                                    placeholder="Ex: Alan Turing"
                                    required
                                    value={formData.name}
                                    onChange={handleChange}
                                    disabled={isLoading}
                                    className="bg-white dark:bg-zinc-950"
                                />
                            </div>

                            <div className="space-y-2">
                                <Label htmlFor="companyName">Nome da Empresa (Workspace)</Label>
                                <Input
                                    id="companyName"
                                    placeholder="Sua Consultoria ou Nome Pessoal"
                                    required
                                    value={formData.companyName}
                                    onChange={handleChange}
                                    disabled={isLoading}
                                    className="bg-white dark:bg-zinc-950"
                                />
                            </div>

                            <div className="space-y-2">
                                <Label htmlFor="email">E-mail Profissional</Label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="alan@turing.com"
                                    required
                                    value={formData.email}
                                    onChange={handleChange}
                                    disabled={isLoading}
                                    className="bg-white dark:bg-zinc-950"
                                />
                            </div>

                            <div className="space-y-2">
                                <Label htmlFor="password">Senha Forte</Label>
                                <Input
                                    id="password"
                                    type="password"
                                    required
                                    value={formData.password}
                                    onChange={handleChange}
                                    disabled={isLoading}
                                    className="bg-white dark:bg-zinc-950"
                                />
                            </div>

                            <div className="space-y-2">
                                <Label htmlFor="confirmPassword">Confirmar Senha</Label>
                                <Input
                                    id="confirmPassword"
                                    type="password"
                                    required
                                    value={formData.confirmPassword}
                                    onChange={handleChange}
                                    disabled={isLoading}
                                    className="bg-white dark:bg-zinc-950"
                                />
                            </div>
                        </CardContent>
                        <CardFooter className="flex flex-col space-y-4 pt-4">
                            <Button
                                type="submit"
                                className="w-full bg-emerald-600 hover:bg-emerald-700 text-white dark:bg-emerald-600 dark:hover:bg-emerald-700"
                                disabled={isLoading}
                            >
                                {isLoading ? 'Criando Conta...' : 'Come├ºar Agora'}
                            </Button>
                            <div className="text-center text-sm text-zinc-500 dark:text-zinc-400">
                                J├í tem uma conta? <Link to="/login" className="font-semibold text-emerald-600 hover:underline dark:text-emerald-400">Entrar</Link>
                            </div>
                        </CardFooter>
                    </form>
                </Card>
            </div>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TopNav } from '../../components/layout/TopNav';
import { TimerDisplay } from '../../components/tracker/TimerDisplay';
import { ProjectSelector } from '../../components/tracker/ProjectSelector';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Play, Square } from 'lucide-react';
import { api } from '../../lib/api';

export const ZenTimer: React.FC = () => {
    const [isZenMode, setIsZenMode] = useState(false);
    const [taskDescription, setTaskDescription] = useState('');
    const [selectedProjectId, setSelectedProjectId] = useState<string | null>(null);
    const [startTime, setStartTime] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        checkActiveTimer();
    }, []);

    const checkActiveTimer = async () => {
        try {
            const res = await api.get('/time-entries/active');
            if (res.data && res.data.id) {
                // Resume active timer from backend
                setIsZenMode(true);
                setTaskDescription(res.data.description || '');
                setSelectedProjectId(res.data.projectId);
                setStartTime(res.data.startTime);
            }
        } catch (error: any) {
            // Se retornar 204 No Content, ├® porque n├úo tem timer rodando
            if (error.response?.status !== 204) {
                console.error('Falha ao verificar timer ativo:', error);
            }
        } finally {
            setIsLoading(false);
        }
    };

    const handleStart = async () => {
        if (!selectedProjectId) return;
        setError(null);
        try {
            const res = await api.post('/time-entries/start', {
                projectId: selectedProjectId,
                description: taskDescription
            });
            setStartTime(res.data.startTime);
            setIsZenMode(true);
        } catch (err: unknown) {
            if (axios.isAxiosError(err)) {
                setError(err.response?.data?.message || 'Falha ao iniciar o timer.');
            } else {
                setError('Erro inesperado ao iniciar o timer.');
            }
        }
    };

    const handleStop = async () => {
        setError(null);
        try {
            await api.put('/time-entries/stop');
            setIsZenMode(false);
            setStartTime(null);
            setTaskDescription('');
            setSelectedProjectId(null);
        } catch (err: unknown) {
            if (axios.isAxiosError(err)) {
                setError(err.response?.data?.message || 'Falha ao parar o timer.');
            } else {
                setError('Erro inesperado ao parar o timer.');
            }
        }
    };

    if (isLoading) {
        return (
            <div className="flex min-h-screen items-center justify-center bg-zinc-50 dark:bg-zinc-950">
                <div className="h-8 w-8 animate-spin rounded-full border-4 border-emerald-500 border-t-transparent"></div>
            </div>
        );
    }

    return (
        <div className="flex flex-col min-h-screen bg-zinc-50 dark:bg-zinc-950 transition-colors duration-500 overflow-hidden">
            {/* Navbar transitions out when Zen Mode is active */}
            <div className={`transition-all duration-700 ease-in-out origin-top ${isZenMode ? 'opacity-0 h-0 overflow-hidden' : 'opacity-100 h-16'}`}>
                <TopNav />
            </div>

            <main className="flex-1 container mx-auto p-4 md:p-8 flex flex-col items-center justify-center">
                <div className="w-full max-w-3xl space-y-8 flex flex-col items-center transition-all duration-700">

                    {/* Error Banner */}
                    {error && (
                        <div className="w-full max-w-md rounded-lg bg-red-50 dark:bg-red-900/30 p-4 text-sm text-red-600 dark:text-red-400 text-center animate-in fade-in duration-300">
                            {error}
                        </div>
                    )}

                    {/* The Giant Timer */}
                    <TimerDisplay
                        isActive={isZenMode}
                        startTime={startTime}
                    />

                    {/* Display task name as text in Zen mode */}
                    <div className={`text-xl md:text-3xl text-zinc-600 dark:text-zinc-400 font-medium transition-all duration-700 text-center ${isZenMode ? 'opacity-100 translate-y-0 h-auto' : 'opacity-0 h-0 overflow-hidden -translate-y-4'}`}>
                        {taskDescription || 'Foco Profundo'}
                    </div>

                    {/* Start controls & Input (Idle Mode) */}
                    <div className={`w-full flex flex-col md:flex-row gap-3 md:gap-4 items-center justify-center transition-all duration-700 ${isZenMode ? 'opacity-0 h-0 overflow-hidden scale-95' : 'opacity-100 scale-100'}`}>
                        <div className="w-full md:w-auto">
                            <ProjectSelector
                                selectedProjectId={selectedProjectId}
                                onSelect={setSelectedProjectId}
                                disabled={isZenMode}
                            />
                        </div>
                        <Input
                            id="taskDescription"
                            name="taskDescription"
                            value={taskDescription}
                            onChange={(e) => setTaskDescription(e.target.value)}
                            placeholder="O que voc├¬ est├í construindo agora?"
                            className="w-full md:flex-1 h-14 md:h-16 text-lg md:text-xl px-6 border-transparent focus-visible:ring-1 focus-visible:ring-emerald-500 bg-white dark:bg-zinc-900 text-left shadow-sm rounded-full"
                        />
                        <Button
                            onClick={handleStart}
                            size="lg"
                            className="w-full md:w-auto h-14 md:h-16 px-8 md:px-10 rounded-full bg-emerald-600 hover:bg-emerald-700 text-white text-lg shadow-lg hover:shadow-emerald-500/25 disabled:opacity-50 transition-all flex-shrink-0"
                            disabled={!selectedProjectId} // Frontend validation for required project
                        >
                            <Play className="mr-2 h-5 w-5 fill-current" /> Iniciar
                        </Button>
                    </div>

                    {/* Stop control (Zen Mode) */}
                    <div className={`transition-all duration-700 ${isZenMode ? 'opacity-100 scale-100 pt-8' : 'opacity-0 h-0 pt-0 overflow-hidden scale-95'}`}>
                        <Button
                            onClick={handleStop}
                            size="lg"
                            variant="destructive"
                            className="h-16 w-16 p-0 rounded-full shadow-lg hover:shadow-red-500/25 hover:-translate-y-1 transition-all"
                        >
                            <Square className="h-6 w-6 fill-current" />
                        </Button>
                    </div>
                </div>
            </main>
        </div>
    );
};
